<script>
jQuery(document).ready(function($) {
    // Track feature interactions
    $('.feature-item').on('click', function() {
        var featureTitle = $(this).find('.feature-title').text();
        var featureType = $(this).data('feature');
        
        // Analytics tracking
        if (typeof gtag !== 'undefined') {
            gtag('event', 'feature_viewed', {
                event_category: 'about_island',
                event_label: featureType,
                feature_title: featureTitle
            });
        }
        
        // Microsoft Clarity tracking
        if (typeof clarity !== 'undefined') {
            clarity('event', 'about_feature_interaction', {
                feature_type: featureType,
                feature_title: featureTitle
            });
        }
    });

    // Intersection Observer for fade-in animation
    if ('IntersectionObserver' in window) {
        const observerOptions = {
            threshold: 0.1,
            rootMargin: '0px 0px -50px 0px'
        };

        const observer = new IntersectionObserver(function(entries) {
            entries.forEach(function(entry) {
                if (entry.isIntersecting) {
                    entry.target.classList.add('animate-in');
                }
            });
        }, observerOptions);

        // Observe feature items
        $('.feature-item').each(function() {
            observer.observe(this);
        });
    }
});
</script>