<script>
jQuery(document).ready(function($) {
    // Track accommodation card interactions
    $('.accommodation-card').on('click', function(e) {
        var accommodationType = $(this).data('accommodation');
        var cardTitle = $(this).find('.accommodation-title').text();
        
        // Analytics tracking
        if (typeof gtag !== 'undefined') {
            gtag('event', 'accommodation_card_viewed', {
                event_category: 'accommodations',
                event_label: accommodationType,
                accommodation_title: cardTitle
            });
        }
        
        // Microsoft Clarity tracking
        if (typeof clarity !== 'undefined') {
            clarity('event', 'accommodation_interaction', {
                type: accommodationType,
                title: cardTitle
            });
        }
    });

    // Track CTA button clicks
    $('.accommodation-button').on('click', function() {
        var buttonText = $(this).text();
        var accommodationType = $(this).closest('.accommodation-card').data('accommodation');
        var buttonUrl = $(this).attr('href');
        
        // Analytics tracking
        if (typeof gtag !== 'undefined') {
            gtag('event', 'accommodation_cta_click', {
                event_category: 'accommodations',
                event_label: accommodationType,
                button_text: buttonText,
                url: buttonUrl
            });
        }
        
        // Microsoft Clarity tracking
        if (typeof clarity !== 'undefined') {
            clarity('event', 'accommodation_cta_click', {
                type: accommodationType,
                text: buttonText,
                url: buttonUrl
            });
        }
    });

    // Intersection Observer for animations
    if ('IntersectionObserver' in window) {
        const observerOptions = {
            threshold: 0.2,
            rootMargin: '0px 0px -50px 0px'
        };

        const observer = new IntersectionObserver(function(entries) {
            entries.forEach(function(entry) {
                if (entry.isIntersecting) {
                    entry.target.classList.add('animate-in');
                }
            });
        }, observerOptions);

        // Observe accommodation cards
        $('.accommodation-card').each(function() {
            observer.observe(this);
        });
    }

    // Hover effects for amenity items
    $('.amenity-item').on('mouseenter', function() {
        $(this).addClass('hovered');
    }).on('mouseleave', function() {
        $(this).removeClass('hovered');
    });
});
</script>